import { useState, useEffect } from 'react';

function App() {
    const [mode, setMode] = useState(0);

    function SwitchMode() {
        setMode(prevMode => (prevMode === 0 ? 1 : 0));
    }

    return (
        <div>
            {mode === 0 ? <App1 /> : <App2 />}
            <button onClick={SwitchMode}>Switch Mode</button>
        </div>
    );
}

function App1() {
  const [time, setTime] = useState(0);
  const [paused, setPaused] = useState(true);

  useEffect(() => {
    const intervalId = setInterval(() => {
      if (!paused) {
        setTime((prevTime) => prevTime + 1);
      }
    }, 1000);

    return () => clearInterval(intervalId);
  }, [paused]);

  function Start() {
    setPaused((prevPaused) => !prevPaused);
  }

  function Reset() {
    setTime(0);
  }

  return (
    <div className="App">
      <h1>Stopwatch</h1>
      <h2>{time}</h2>
      <button onClick={Start}>Start/Stop</button>
      <button onClick={Reset}>Reset</button>
    </div>
  );
}

function App2() {
  const [count, setCount] = useState(0);
  const [increasing, setIncreasing] = useState(true);
  const [paused, setPaused] = useState(false);
  const [pause, setPause] = useState("Running");

  useEffect(() => {
    if (paused) setPause(pause => "Paused");
    else if (increasing) setPause(pause => "Increasing");
    else setPause(pause => "Decreasing");

    const interval = setInterval(Add, 1000);
    return () => clearInterval(interval);
  }, [count, increasing, paused]);

  function Add() {
    if (!paused) {
      if (increasing) setCount((count) => count + 1);
      else setCount((count) => count - 1);
    }
  }

  function Plus() {
    setIncreasing((increasing) => true);
  }

  function Minus() {
    setIncreasing((increasing) => false);
  }

  function Pause() {
    setPaused((paused) => !paused);
  }

  return (
    <div className="App">
      <h1>Counter</h1>
      <h2>{count} {pause}</h2>
      <button onClick={Plus}>Count Up</button>
      <button onClick={Minus}>Count Down</button>
      <button onClick={Pause}>Stop/Continue</button>
    </div>
  );
}

export default App;
